
public class Forloopclass {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
				
			int i=12;
			int j=10;
			
			while (i<=24)
					
				{
					System.out.println("The time" +" " + i);
					System.out.println("The temp" +" " + j);
					System.out.println("");
	
					i++;
					j=j-2;
					
				}
				
			}

			
		}

		
	


